import React from "react";


export default class RedirectHomeButton extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            shouldRedirect: false
        }

    }

    render(){

        return(
            <div>
                <button onClick={() => this.setState({shouldRedirect: true})}>
                    Return Home
                </button>
                {this.state.shouldRedirect && <Navigate to="/" />}
            </div>
        )
    }
    }
